import React, { Component } from 'react';
import axios from 'axios';
import { Button, Form, Container, Row, Col, Jumbotron, Badge, Toast, ToastHeader, ToastBody  } from 'react-bootstrap';
import * as Constants from '../../constants';
class AddUsers extends Component {

state = {
    fname: "",
    lname: "", 
    email: "",
    mobile: "",
    add1:"",
    add2:"",
    role:"",
    status: 4,
    isAddSuccess: null,
    error: {}
  }

handleSubmit = (event) => {
    event.preventDefault();

    if (this.state.email.length > 0) {
          
        var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
        if (!pattern.test(this.state.email)) {
          errors["email"] = "Please enter valid email address.";
        }
      }


    let addReq = {
        firstname: this.state.fname,
        lastname: this.state.lname,
        email: this.state.email,
        mobile: this.state.mobile,
        address1: this.state.add1,
        address2: this.state.add2,
        userrole: this.state.role,
        status: this.state.status,
      };
      axios({
        method: "post",
        url: Constants.API_URL + "/Api/UserInfo",
        data: addReq
      }).then(resp =>  {
          console.log(resp);
          if(resp.status === 200)
          {
            this.setState({ 
                fname: "",
                lname: "", 
                email: "",
                mobile: "",
                add1:"",
                add2:"",
                role:"",
                status: 4,
                isAddSuccess: true
            });
          }
          else{
              this.setState({isAddSuccess: false});
          }
       
      });
}

validateForm = () =>  {
    return this.state.fname.length > 0 
    && this.state.lname.length > 0
    && this.state.email.length > 0
    && this.state.mobile.length > 0
    && this.state.add1.length > 0
    && this.state.add2.length > 0
    && this.state.role.length > 0;
  }

render(){
        return(
            <div>
                <Container>
                    <Row>
                        <Col>
                            <h1>Add Users</h1>
                        </Col>
                    </Row>
                    <Row>
                        <Col>
                            <Jumbotron>
                                {
                                    this.state.isAddSuccess === null ?
                                    console.log("init")
                                    :
                                    this.state.isAddSuccess ?
                                    <Badge variant="success" pill>Added user sucessfully</Badge>
                                    :
                                    <Badge variant="danger" pill>Falied adding user</Badge> 
                                }
                                <Form onSubmit={this.handleSubmit}>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formGridFirstName">
                                        <Form.Label>First Name</Form.Label>
                                        <Form.Control type="text" 
                                         value={this.state.fname}
                                         onChange={e => this.setState({fname:e.target.value})}
                                        placeholder="Enter First Name" />
                                        </Form.Group>

                                        <Form.Group as={Col} controlId="formGridLastName">
                                        <Form.Label>LastName</Form.Label>
                                        <Form.Control type="text" 
                                        value={this.state.lname}
                                        onChange={e => this.setState({lname:e.target.value})}
                                        placeholder="Enter Last Name" />
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formGridEmail">
                                        <Form.Label>Email</Form.Label>
                                        <Form.Control type="email" 
                                        value={this.state.email}
                                        onChange={e => this.setState({email:e.target.value})}
                                        placeholder="Enter email" />
                                        <div className="text-danger">{this.state.errors.email}</div>
                                        </Form.Group>

                                        <Form.Group as={Col} controlId="formGridMobile">
                                        <Form.Label>Mobile</Form.Label>
                                        <Form.Control type="number" 
                                        value={this.state.mobile}
                                        onChange={e => this.setState({mobile:e.target.value})}
                                        placeholder="Enter Mobile Number" />
                                        </Form.Group>
                                    </Form.Row>

                                    <Form.Group controlId="formGridAddress1">
                                        <Form.Label>Address</Form.Label>
                                        <Form.Control 
                                        value={this.state.add1}
                                        onChange={e => this.setState({add1:e.target.value})}
                                        placeholder="1234 Main St" />
                                    </Form.Group>

                                    <Form.Group controlId="formGridAddress2">
                                        <Form.Label>Address 2</Form.Label>
                                        <Form.Control 
                                        value={this.state.add2}
                                        onChange={e => this.setState({add2:e.target.value})}
                                        placeholder="Apartment, studio, or floor" />
                                    </Form.Group>

                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formGridRole">
                                        <Form.Label>Role</Form.Label>
                                        <Form.Control as="select" 
                                        value={this.state.role}
                                        onChange={e => this.setState({role:e.target.value})}
                                        defaultValue="Select Role">
                                            <option>Select Role</option>
                                            <option>NormalUser</option>
                                            <option>AdminUser</option>
                                            <option>SuperUser</option>
                                        </Form.Control>
                                        </Form.Group>
                                    </Form.Row>
                                    <Button block disabled={!this.validateForm()} type="submit">
                                        Submit
                                    </Button>
                                </Form>
                            </Jumbotron>
                        </Col>
                    </Row>
                </Container>
                
            </div>
        )
    }
}

export default AddUsers;
