import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Navbar, Nav, Form } from 'react-bootstrap';
import * as Constants from "../../constants";

class UserNav extends Component {
state = {
    loggedinuser: ""
}
componentDidMount()
{
    this.setState({ 
        loggedinuser : this.props.userrole.toString().toLowerCase() === Constants.NORMAL ? "Normal User" 
        :(this.props.userrole.toString().toLowerCase() === Constants.ADMIN ?  "Admin User" : "Super User") 
    });
}

render(){
        return(
            <Navbar bg="dark" variant="dark">
                <Navbar.Brand href="#home">
                    <strong className="mr-auto">User Admin Module</strong>
                </Navbar.Brand>
                <Nav className="mr-auto">
                
                </Nav>
                <Form inline>
                    {
                        this.state.loggedinuser === "" ? ""
                        :
                        <Navbar.Brand href="#home">Logged in as - [ {this.state.loggedinuser} ]</Navbar.Brand>
                    }
                    <Link variant="outline-info" to="/">Logout</Link>
                    {/* <Button variant="outline-info" onClick={() => history.push('/')}>Logout</Button> */}
                </Form>
          </Navbar>
        )
    }
}

export default UserNav;
