import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import * as Constants from '../../constants';
import { Button, Form, Container, Row, Col, Jumbotron, Alert } from 'react-bootstrap';
import '../login/login.css';

class Login extends Component {
  state = {
    redirect: false,
    userrole: "",
    isloggedin: null
  }

  validateForm = () =>  {
    return this.state.userrole.length > 0;
  }

  handleSubmit = (event) => {
    event.preventDefault();
    this.setState({ redirect: true, isloggedin: true});
  }

  render () {
    const { redirect, userrole, isloggedin } = this.state;
   
     if (redirect) {
        if(userrole.toString().toLowerCase() === Constants.NORMAL){
          return <Redirect 
          to={{
                pathname: "/adduser",
                state: { userrole: userrole, isloggedin: isloggedin }
              }}
          />;
        }
        else if(userrole.toString().toLowerCase() === Constants.ADMIN
        || userrole.toString().toLowerCase() === Constants.SUPER){
          return <Redirect
          to={{
            pathname: "/user",
            state: { userrole: userrole, isloggedin: isloggedin }
          }}
          />;
        }
     }

     return (
      <div className="Login">
          <Container>
            <Row>
              <Col>
                <h2><Form.Label>Login</Form.Label></h2>
              </Col>
            </Row>
            <Row>
              <Col>
                <Jumbotron>
                  <form onSubmit={this.handleSubmit}>
                    <Form.Group controlId="userrole">
                      <Form.Control as="select" 
                      value={this.state.userrole}
                      onChange={e => this.setState({userrole: e.target.value})}>
                      <option>Select role</option>
                      <option>NormalUser</option>
                      <option>AdminUser</option>
                      <option>SuperUser</option>
                      </Form.Control>
                    </Form.Group>
                    <Button block disabled={!this.validateForm()} type="submit">
                      Login
                    </Button>
                  </form>
                </Jumbotron>
              </Col>
            </Row>
          </Container>  
      </div>
    );
  }
} 


export default Login;
